package org.yang.web.view.controls.jsStyle;
import javax.servlet.jsp.JspWriter;
import java.util.HashSet;

public class CheckBoxes extends FormElement 
{
   private String id = "";
   public void setId(String id) { this.id = id; }

   private String name = null;
   public void setName(String name) { this.name = name; }

   private String[] values = null;
   public void setValues(String[] values) { this.values = values; }

   private String[] checkedValues = null;
   public void setCheckedValues(String[] checkedValues) { this.checkedValues = checkedValues; }

   private String[] displayNames = null;
   public void setDisplayNames(String[] displayNames) { this.displayNames = displayNames; }

   public void printBody(JspWriter writer) throws Exception
   {
      HashSet checked = new HashSet();
      for(int i=0; i<checkedValues.length; i++)
      {
         checked.add(checkedValues[i]);
      }

      for(int i=0; i<values.length; i++)
      {
         if(checked.contains(values[i]))
            writer.println( "<input id=\"" + id + i + "\" type=\"checkbox\" name=\"" + name + "\" value=\"" + values[i] + "\" checked  >" + displayNames[i] + "</input>");
         else
            writer.println( "<input id=\"" + id + i + "\" type=\"checkbox\" name=\"" + name + "\" value=\"" + values[i] + " \" >" + displayNames[i] + "</input>");
      }
   }
}